        marginRight: 20,
        marginLeft: 20,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center"
    },
    hubStats: {
        justifyContent: "center",
        width: "40%",
        height: "60%",
        paddingLeft: 10
    },
    editBox: {
      position: "absolute",
      flexDirection: "row",
      alignItems: "center",
      right: 15,
      justifyContent: "flex-end",
      width: 50,
      height: 25,
    },
    gear: {
        height: 16,
        width: 16,
        marginRight: 5
    },
    header: {
      color: "gray",
      fontSize: 20
    },
    headerLine: {
        flexDirection: "row",
        alignItems: "center",
        marginBottom: 10
    },
    hardwareType: {
        color: "white",
        fontWeight: 'bold',
        fontSize: 16
    },
    softwareType: {
        color: "white",
        fontSize: 13
    },
    status: {
        color: "#2DC62A",
        marginBottom: 5
    },
    noHub: {
        height: "60%",
        alignItems: "center",
        justifyContent: "center"
    }
  });


export default YourHubCard;


