                  var withinTimeFrame = 0;
                  var daysReoccuring = temp.time_range_reoccuring.match(
                    /.{1,3}/g
                  );
                  for (var i = 0; i < daysReoccuring.length; i++) {
                    if (day === daysReoccuring[i]) {
                      withinTimeFrame = 1;
