                        });
                    }
                  } else {
                    this.showToast("Can't use this device on this day");
                    console.log("Can't use this device on this day");
                  }
                }
              } else {
                this.showToast(
                  'This device is not available at this time (Too late or wrong day)'
                );
                console.log(
                  'This device is not available at this time (Too late or wrong day)'
